var title_f21_61_22492="Treatment of Pneumocystis infection";
var content_f21_61_22492=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F61052&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F61052&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Drugs used in the treatment of Pneumocystis pneumonia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Drug",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dosage",
"      </td>",
"      <td class=\"subtitle1\">",
"       Major adverse reactions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Preferred regimen",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       TMP-SMX",
"      </td>",
"      <td>",
"       Not acutely ill (able to take oral medication; PaO2&gt;70 mm Hg): Two double strength TMP-SMX tabs orally every 8 hours",
"      </td>",
"      <td rowspan=\"2\">",
"       Rash, fever, neutropenia, hyperkalemia, transaminase elevation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <p>",
"        Acutely ill (not able to take oral medicaion; PaO2&lt;70 mm Hg): TMP-SMX (15 mg/kg of TMP component per day) intravenously divided every 6 to 8 hours",
"       </p>",
"       <p>",
"        PLUS",
"       </p>",
"       <p>",
"        Prednisone* taper:",
"       </p>",
"       <p>",
"        40 mg PO twice daily for 5 days",
"       </p>",
"       <p>",
"        40 mg PO once daily for 5 days",
"       </p>",
"       <p>",
"        20 mg PO once daily for 11 days",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Alternative regimens",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Pentamidine",
"      </td>",
"      <td>",
"       4 mg/kg/day IV once daily",
"      </td>",
"      <td>",
"       Nephrotoxicity, hyperkalemia, hypoglycemia, hypotension, pancreatitis, dysrhythmias, transaminase elevation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Atovaquone",
"      </td>",
"      <td>",
"       750 mg PO twice daily",
"      </td>",
"      <td>",
"       Rash, fever, transaminase elevation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       TMP plus dapsone",
"      </td>",
"      <td>",
"       <p>",
"        TMP: 5 mg/kg PO three times daily",
"       </p>",
"       <p>",
"        Dapsone: 100 mg/day PO once daily",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Trimethoprim: Rash, gastrointestinal distress, transaminase elevation, neutropenia",
"       </p>",
"       <p>",
"        Dapsone: Rash, fever, gastrointestinal upset, methemoglobinemia, hemolytic anemia, (check for G6PD deficiency)",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Primaquine plus clindamycin",
"      </td>",
"      <td>",
"       <p>",
"        Primaquine: 15-30 mg/day PO once daily",
"       </p>",
"       <p>",
"        Clindamycin: 600 mg IV every 8 hours",
"        <strong>",
"         OR",
"        </strong>",
"        300-450 mg PO four times daily",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Primaquine: Rash, fever, methemoglobinemia, hemolytic anemia (check for G6PD deficiency)",
"       </p>",
"       <p>",
"        Clindamycin: Rash, diarrhea, Clostridium difficile colitis, abdominal pain",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Patients should receive 21 days of therapy.",
"    <div class=\"footnotes\">",
"     TMP-SMX: Trimethoprim-sulfamethoxazole.",
"     <br>",
"      * Adjunctive glucocorticoids should be given to patients with a room air PA02 &lt;/=70 mmHg or alveolar-arterial oxygen gradient &gt;/=35 mmHg.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
