var title_f22_1_22556="Depression in AD";
var content_f22_1_22556=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F58421&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F58421&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Depression in Alzheimer-type dementia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"1\" rowspan=\"1\">",
"        A. Three or more of the following",
"symptoms, present during the same two-week period, and representing a",
"change from a previous level of functioning. Either item-one or",
"item-two must be included:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        1. Clinically significant depressed mood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        2. Decreased positive affect or pleasure in response to social contacts and usual activities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        3. Social isolation or withdrawal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        4. Disturbed appetite",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        5. Disturbed sleep",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        6. Psychomotor retardation or agitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        7. Irritability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        8. Fatigue or loss of energy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        9. Feelings of worthlessness, hopelessness, or inappropriate guilt",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        10. Recurrent thoughts of death or suicidal ideation, plan, or any attempt&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. Meets criteria for Alzheimer-type dementia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        C. Depressive symptoms cause clinically significant distress or disruption in function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D. Symptoms do not occur exclusively during an episode of delirium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        E. Symptoms are not due to a direct physiological effect from a substance (medication or drug of abuse)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        F. Symptoms are not better accounted for by another condition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Specify if:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Co-occuring onset: onset antedates or co-occurs with AD symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Post-AD onset: onset occurs after AD diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Specify if:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\" rowspan=\"1\">",
"        With psychosis of AD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        With other significant behaviorial signs or symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        With past history of mood disorder",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
