var title_f22_14_22763="Chemo escalate for desensitiz";
var content_f22_14_22763=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F61477&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F61477&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Desensitization protocols",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Platinum administration",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Mix calculated total dose in 100 cc D5W.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Administer 0.1 cc of this solution in 100 cc D5W over 1 h (1:1000 dilution).",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If tolerated, administer 1 cc of the solution in 100 cc D5W over 1 h (1:100 dilution).",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If tolerated, administer 10 cc of the solution in 100 cc D5W over 1 h (1:10 dilution).",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If tolerated, administer the remaining solution over 1 h (1:1 dilution)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       OR: Administer at 25 percent of usual rate for 1 h.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       If tolerated, administer at 50 percent of the usual rate for 1 h.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       If tolerated, administer at 75 percent of the usual rate for 1 h.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       If tolerated, administer the remaining at usual rate.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Robinson, JB, Singh, D, Bodurka-Bevers, DC, et al. Hypersensitivity reactions and the utility of oral and intravenous desensitization in patients with gynecologic malignancies. Gynecologic Oncol 2001; 82:555. Copyright &copy;2001 Elsevier Science.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
