" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Prognosis after cardiac transplantation",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/15/22775/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?22/15/22775/contributors\">",
"     Allen S Anderson, MD, FACC",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/15/22775/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?22/15/22775/contributors\">",
"     Sharon A Hunt, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
