var title_f22_15_22779="Avoiding bias screening";
var content_f22_15_22779=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F57081&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F57081&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Avoiding bias in screening",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Bias",
"       </td>",
"       <td class=\"subtitle1\">",
"        Effect",
"       </td>",
"       <td class=\"subtitle1\">",
"        How to avoid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lead time",
"       </td>",
"       <td>",
"        Appears to improve survival time but actually increases \"disease\" time after disease detection",
"       </td>",
"       <td>",
"        Use mortality rather than survival rates.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Length time",
"       </td>",
"       <td>",
"        Outcome appears better in screened group because more cancers with a good prognosis are detected",
"       </td>",
"       <td>",
"        Compare outcomes in RCT with control group and one offered screening. Count all outcomes regardless of method of detection.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Compliance",
"       </td>",
"       <td>",
"        Outcome in screened group appears better due to compliance, not screening",
"       </td>",
"       <td>",
"        Compare outcomes in RCT with control group and one offered screening. Count all outcomes regardless of compliance.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Fletcher RH, Fletcher SW. Prevention. In: Clinical Epidemiology - The Essentials, 4th ed. Lippincott Williams &amp; Wilkins, Baltimore 2005. Copyright &copy; 2005 Lippincott Williams and Wilkins.",
"    </div>",
"    <div class=\"contractual\">",
"     <br/>",
"     <a href=\"file://www.lww.com\">",
"      file://www.lww.com",
"     </a>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
