var title_f22_19_22844="Severity of AR in adults";
var content_f22_19_22844=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F53536&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F53536&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Severity of aortic regurgitation (AR) in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mild",
"       </td>",
"       <td class=\"subtitle1\">",
"        Moderate",
"       </td>",
"       <td class=\"subtitle1\">",
"        Severe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\" rowspan=\"1\">",
"        Structural parameters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Left ventricular size",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Normal or dilated",
"       </td>",
"       <td>",
"        Dilated, except acute AR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aortic leaflets",
"       </td>",
"       <td>",
"        Normal or abnormal",
"       </td>",
"       <td>",
"        Normal or abnormal",
"       </td>",
"       <td>",
"        Abnormal/flail, or wide coaptation defect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\" rowspan=\"1\">",
"        Doppler parameters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Color Doppler jet width",
"       </td>",
"       <td>",
"        Central jet, width &lt;25 percent of LVOT",
"       </td>",
"       <td>",
"        Central jet, width 25 to 65 percent of LVOT",
"       </td>",
"       <td>",
"        Central jet, width &gt;65 percent of LVOT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Doppler vena contracta width",
"       </td>",
"       <td>",
"        &lt;3 mm",
"       </td>",
"       <td>",
"        3 to 6 mm",
"       </td>",
"       <td>",
"        &gt;6 mm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\" rowspan=\"1\">",
"        Quantitative parameters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Regurgitant volume",
"       </td>",
"       <td>",
"        &lt;30 mL/beat",
"       </td>",
"       <td>",
"        30 to 59 mL/beat",
"       </td>",
"       <td>",
"        &ge;60 mL/beat",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Regurgitant fraction",
"       </td>",
"       <td>",
"        &lt;30 percent",
"       </td>",
"       <td>",
"        30 to 49 percent",
"       </td>",
"       <td>",
"        &ge;50 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Regurgitant orifice area",
"       </td>",
"       <td>",
"        &lt;0.10 cm2",
"       </td>",
"       <td>",
"        0.10 to 0.29 cm2",
"       </td>",
"       <td>",
"        &ge;0.30 cm2",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     LVOT: left ventricular outflow tract.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Zoghbi WA, Enriquez-Sarano M, Foster E, et al. American Society of Echocardiography Report. Recommendations for evaluation of the severity of native valvular regurgitation with two-dimensional and Doppler echocardiography. J Am Soc Echocardiogr 2003; 16:777; and Bonow RO, Carabello BA, Chatterjee K, et al. ACC/AHA 2006 guidelines for the management of patients with valvular heart disease. A report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing committee to revise the 1998 guidelines for the management of patients with valvular heart disease). J Am Coll Cardiol 2006; 48:e1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
