"     <li>",
"      Etidron (IT);",
"     </li>",
"     <li>",
"      Etidronat Jenapharm (DE);",
"     </li>",
"     <li>",
"      Oestrodidronel (BE);",
"     </li>",
"     <li>",
"      Ostedron (PL);",
"     </li>",
"     <li>",
"      Osteotop (CN, PE);",
"     </li>",
"     <li>",
"      Osteum (ES);",
"     </li>",
