var title_f22_23_22911="Standing thigh stretch";
var content_f22_23_22911=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F75432&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F75432&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 470px\">",
"   <div class=\"ttl\">",
"    Flexibility exercise: Thigh (standing)",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 369px; height: 297px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    Here's an exercise that stretches your thigh muscles. If you've had hip or back surgery, talk with your doctor before trying this stretch.",
"    <ol>",
"     <li>",
"      Stand behind a sturdy chair with your feet shoulder-width apart and your knees straight, but not locked.",
"     </li>",
"     <li>",
"      Hold on to the chair for balance with your right hand.",
"     </li>",
"     <li>",
"      Bend your left leg back and grab your foot in your left hand. Keep your knee pointed to the floor. If you can't grab your ankle, loop a resistance band, belt, or towel around your foot and hold both ends.",
"     </li>",
"     <li>",
"      Gently pull your leg until you feel a stretch in your thigh.",
"     </li>",
"     <li>",
"      Hold position for 10 to 30 seconds.",
"     </li>",
"     <li>",
"      Repeat at least three to five times.",
"     </li>",
"     <li>",
"      Repeat at least three to five times with your right leg.",
"     </li>",
"    </ol>",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Go4Life. National Institute on Aging at the National Institutes of Health. Available at: file://go4life.niapublications.org (Accessed December 2, 2011).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
