var title_f22_25_22939="Histologic features LIP";
var content_f22_25_22939=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F69805&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F69805&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histologic features of lymphoid interstitial pneumonia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Key histologic features",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Diffuse interstitial infiltration of involved areas.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Predominantly alveolar septal distribution.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Infiltrates compromised mostly of T and/or B lymphocytes, plasma cells, and macrophages.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lymphoid hyperplasia (mucosa-associated lymphoid tissue (MALT) hyperplasia) - frequent.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Pertinent negative findings",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of tracking along lymphatic routes (bronchovascular bundles, pleura, and interlobular septa), characteristic of lymphomas.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Organizing pneumonia, inconspicuous or absent.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of Dutcher bodies (ie, intranuclear vacuoles containing IgM monoclonal protein seen in Waldenstrom's macroglobulinemia).",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of monoclonal light chain staining pattern of plasma cells (polyclonal pattern present).",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of extensive pleural involvement or lymph node involvement.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of necrotizing granulomas.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
