var title_f22_25_22941="Epidemiologic and historic clues encephalitis";
var content_f22_25_22941=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F57122&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F57122&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Epidemiologic and historic clues to the etiology of encephalitis in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Epidemiologic clues",
"       </td>",
"       <td class=\"subtitle1_single\">",
"        Potential etiologies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Age (0 to 28 days)",
"       </td>",
"       <td>",
"        <p>",
"         Infectious: CMV, HSV-2 or HSV-1, rubella virus",
"        </p>",
"        Noninfectious: Inborn error of metabolism (eg, organic acidemia, urea cycle disorder)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        Season:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Summer",
"       </td>",
"       <td class=\"sublist_other\">",
"        Enterovirus, free living amebae",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Late summer/fall",
"       </td>",
"       <td class=\"sublist_other\">",
"        Arbovirus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Winter",
"       </td>",
"       <td class=\"sublist_other\">",
"        Postinfectious encephalitis in countries with low rates of MMR immunization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infection in horses, birds",
"       </td>",
"       <td>",
"        Arbovirus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blood transfusion or transplant recipient",
"       </td>",
"       <td>",
"        CMV, EBV, HIV, rabies, tick-borne encephalitis, WNV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immunodeficiency",
"       </td>",
"       <td>",
"        CMV, enterovirus, HHV6, HSV, VZV, WNV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Historical clues",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"2\">",
"        Rash",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Vesicular",
"       </td>",
"       <td>",
"        HSV, VZV, enterovirus (hand, foot, and mouth disease), herpes B virus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Hand, foot, mouth",
"       </td>",
"       <td>",
"        Enterovirus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Erythematous macules and papules with cephalocaudad spread",
"       </td>",
"       <td>",
"        Measles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Maculopapular",
"       </td>",
"       <td>",
"        WNV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Maculopapular/petechial begins on ankles and wrists",
"       </td>",
"       <td>",
"        Rocky Mountain spotted fever",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"2\">",
"        Exposures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Mosquitoes",
"       </td>",
"       <td>",
"        Arbovirus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Ticks",
"       </td>",
"       <td>",
"        <em>",
"         Borrelia burgdorferi",
"        </em>",
"        , Powassan virus,",
"        <em>",
"         Rickettsia rickettsii",
"        </em>",
"        , tick-borne encephalitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Animal bite/exposure (dog, bat, cat, birds, livestock, others)",
"       </td>",
"       <td>",
"        Rabies, arboviruses, cat scratch disease, Q fever",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Blood transfusion or transplant recipient",
"       </td>",
"       <td>",
"        CMV, EBV, HIV, rabies, tick-borne encephalitis, WNV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Recent infectious illness",
"       </td>",
"       <td>",
"        ADEM",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"2\">",
"        Recreational activity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Swimming",
"       </td>",
"       <td>",
"        Enteroviruses, free-living amebae",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Spelunking",
"       </td>",
"       <td>",
"        Rabies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Sexual activity",
"       </td>",
"       <td>",
"        HIV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\">",
"        Travel",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"2\">",
"        Immunization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Lack of immunization for specific agent",
"       </td>",
"       <td>",
"        Japanese encephalitis, measles, mumps, rubella, VZV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Recent immunization",
"       </td>",
"       <td>",
"        ADEM",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CMV: cytomegalovirus; HSV: herpes simplex virus; MMR: measles, mumps, rubella; EBV: Epstein-Barr virus; HIV: human immunodeficiency virus; WNV: West Nile virus; HHV6: human herpesvirus 6; VZV: varicella zoster virus; ADEM: acute disseminated encephalomyelitis.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br/>",
"     <ol>",
"      <li>",
"       Willoughby RE, Long SS. Encephalitis, meningoencephalitis, acute disseminated encephalomyelitis, and acute necrotizing encephalopathy. In: Principles and Practice of Pediatric Infectious Diseases, 2nd ed, Long SS, Pickering LK, Prober CG (Eds), Churchill Livingstone, New York 2008. p.310.",
"      </li>",
"      <li>",
"       Tunkel AR, Glaser CA, Bloch KC, et al. The management of encephalitis: clinical practice guidelines by the Infectious Diseases Society of America. Clin Infect Dis 2008; 47:303.",
"      </li>",
"      <li>",
"       Cherry JD, Shields WD, Bronstein DE. Encephalitis and meningoencephalitis. In: Feigin and Cherry's Textbook of Pediatric Infectious Diseases, 6th ed, Feigin RD, Cherry JD, Demmler-Harrison GJ, Kaplan SL (Eds), Saunders, Philadelphia, 2009. p.504.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
