" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Potential indications for hepatocyte transplantation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Inherited metabolic disorders",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Allogeneic (with immunosuppression or tolerization)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Autologous with ex-vivo gene transfer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Acute liver failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        As a \"bridge\" while awaiting liver transplantation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        As definitive therapy in selected cases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Chronic liver failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For improving metabolic function and encephalopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        To \"buy time\" while awaiting liver transplantation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Rescuing patients who require curative doses of hepatic irradiation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        As a part of treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Generating animal models",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For human hepatocyte-specific infections by transplanting human hepatocytes into immunodeficient animals",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
