var title_f22_28_22988="Autoantibodies in SLE";
var content_f22_28_22988=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F72522&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F72522&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical associations of autoantibodies in SLE",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Antigen specificity",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical associations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Prevalence, percent*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        dsDNA",
"       </td>",
"       <td>",
"        Marker for active disease, titers fluctuates with disease activity, correlates best with renal disease",
"       </td>",
"       <td>",
"        40-60",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ssDNA",
"       </td>",
"       <td>",
"        Nonspecific, no clinical utility",
"       </td>",
"       <td>",
"        70",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ro/SSA",
"       </td>",
"       <td>",
"        Subacute cutaneous lupus (75 percent), photosensitivity, neonatal lupus, complement deficiencies",
"       </td>",
"       <td>",
"        40",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        RNP (U1-RNP)",
"       </td>",
"       <td>",
"        SLE generally in conjunction with Sm; in MCTD, required for diagnosis",
"       </td>",
"       <td>",
"        30-40",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        La/SSB",
"       </td>",
"       <td>",
"        <p>",
"         With La, low prevalence of renal disease",
"        </p>",
"        <p>",
"         Neonatal lupus (75 percent)",
"        </p>",
"       </td>",
"       <td>",
"        10-15",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sm",
"       </td>",
"       <td>",
"        <p>",
"         Marker for disease, not generally useful in management;",
"        </p>",
"        <p>",
"         May be associated with CNS disease",
"        </p>",
"       </td>",
"       <td>",
"        About 20",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phospholipids",
"       </td>",
"       <td>",
"        Hypercoagulable state in some patients. No clinical significance in others. Thrombocytopenia, later trimester abortions",
"       </td>",
"       <td>",
"        30",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Histones",
"       </td>",
"       <td>",
"        &gt;95 percent in drug-related lupus. Also present in RA, SLE, reported in",
"systemic sclerosis with pulmonary fibrosis",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ribosomal P",
"       </td>",
"       <td>",
"        Initially associated with",
"psychosis in SLE, more recently with depression",
"       </td>",
"       <td>",
"        10-40",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        KU",
"       </td>",
"       <td>",
"        <p>",
"         SLE, MCTD (European, American population)",
"        </p>",
"        <br/>",
"        <p>",
"         Scleroderma/myositis overlap (Japanese population)",
"        </p>",
"       </td>",
"       <td>",
"        &le;19 , &le;39",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        PCNA",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        3",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Rates vary depending on technique used.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
