" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Cocaine: Acute intoxication",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/30/23018/contributors\">",
