var title_f22_30_23020="Radiation therapy for esophageal cancer";
var content_f22_30_23020=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F80000&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F80000&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Schema for definitive and palliative treatment for esophageal cancer",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Suggested scheme for definitive treatment",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            External beam radiotherapy (EBRT)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            45 to 50 Gy in 1.8 to 2.0 Gy fractions, five times aweek with concurrent chemotherapy followed by brachytherapy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Brachytherapy after EBRT and not concurrent with chemotherapy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            HDR: total dose of 10 Gy, 5 Gy fractions,&nbsp;one fraction per week starting&nbsp;two to three&nbsp;weeks following completion of EBRT",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            LDR: total dose of 20 Gy single course, 0.4 to 1.0 Gy per hour starting&nbsp;two to three&nbsp;weeks after completion of EBRT",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Suggested scheme for palliative treatment",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Recurrence after EBRT or short life expectancy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Brachytherapy* (HDR: 10 to 14 Gy in one to two fractions or LDR: 20 to 40 Gy in one to two fractions at 0.4 to 1.0 Gy per hour)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            No previous EBRT",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            EBRT 30 to 40 Gy in two to three&nbsp;Gy fractions followed by brachytherapy* (HDR: 10 to 14 Gy in one to two fractions or LDR: 20 to 25 Gy in a single course at 0.4 to 1.0 Gy per hour)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            No previous EBRT and life expectancy &gt;6 months",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Guidelines similar to those for definitive therapy above",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     Gy: gray; HDR: high-dose rate; LDR: low-dose rate.",
"     <br>",
"      * All brachytherapy doses specified 1 cm from midsource/mid-dwell position. Applicator diameter of 6&nbsp;to 10 mm is recommended.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Gaspar LE, Nag S, Herskovic,A, et al. American Brachytherapy Society (ABS) consensus guidelines for brachytherapy of esophageal cancer. Int J Radiat Oncol Biol Phys 1997; 38:127.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
