var title_f22_31_23035="Colorado guidelines for concussion management";
var content_f22_31_23035=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F73118&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F73118&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Colorado guideline for concussion management",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Grade 1",
"       </td>",
"       <td class=\"subtitle1\">",
"        Grade 2",
"       </td>",
"       <td class=\"subtitle1\">",
"        Grade 3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        Presentation",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         1. Confusion without amnesia",
"        </p>",
"        <p>",
"         2. No loss of consciousness",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         1. Confusion with amnesia",
"        </p>",
"        <p>",
"         2. No loss of consciousness",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         1. Loss of consciousness of any duration",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Management",
"       </td>",
"       <td>",
"        Evaluate athlete immediately and every 5 minutes. Athlete may",
"return to play if amnesia or symptoms do not appear for 20 minutes.",
"       </td>",
"       <td>",
"        Examine the athlete the next day.  Athlete may return to play after one week if asymptomatic during that time.",
"       </td>",
"       <td>",
"        Transport athlete to the emergency department; athlete may return",
"to play if asymptomatic for 2 weeks and cleared by neurologist or",
"neurosurgeon.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Colorado Medical Society, Report of the Sports Medicine Committee, 1991.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
