" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Congenital cervical anomalies and benign cervical lesions",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/32/23048/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?22/32/23048/contributors\">",
"     Donald Peter Goldstein, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?22/32/23048/contributors\">",
"     Marc R Laufer, MD",
"    </a>",
"    <br/>",
