var title_f22_38_23147="Treatment of shigellosis in adults";
var content_f22_38_23147=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F68382&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F68382&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of shigellosis in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Antibiotic",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Duration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Levofloxacin",
"       </td>",
"       <td>",
"        500 mg orally once daily",
"       </td>",
"       <td>",
"        3 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ciprofloxacin",
"       </td>",
"       <td>",
"        500 mg orally twice daily or 750 mg orally once daily",
"       </td>",
"       <td>",
"        3 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Azithromycin",
"       </td>",
"       <td>",
"        500 mg orally once daily",
"       </td>",
"       <td>",
"        3 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Trimethoprim-sulfamethoxazole (co-trimoxazole)",
"       </td>",
"       <td>",
"        160/800 mg (one double strength tablet) orally twice daily",
"       </td>",
"       <td>",
"        5 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ceftriaxone",
"       </td>",
"       <td>",
"        1 to 2 g intravenously once daily",
"       </td>",
"       <td>",
"        5 days",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    See text for approach to choice of therapy and comments regarding antimicrobial therapy. The duration of therapy is as outlined above; in addition, five to seven days of therapy should be administered to patients with infection due to S. dysenteriae type 1 or patients with HIV coinfection.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
