var title_f22_38_23149="Therapeutic options in psoriatic juvenile idiopathic arthritis";
var content_f22_38_23149=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F64063&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F64063&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Therapeutic options in psoriatic juvenile idiopathic arthritis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Intra-articular glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Triamcinolone hexacetonide",
"       </td>",
"       <td>",
"        0.5 to 1 mg/kg intra-articular, up to 40 mg depending on size of joint. Most effective glucocorticoid for joint injections; not for soft tissue injections.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Methylprednisolone acetate",
"       </td>",
"       <td>",
"        1 mg/kg intra-articular or soft-tissue including dactylitis, up to 80 mg depending upon size of joint or lesion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Nonsteroidal antiinflammatory drugs (NSAIDs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Naproxen",
"       </td>",
"       <td>",
"        5 to 10 mg/kg/dose orally twice daily (maximum 1000 mg/day)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ibuprofen",
"       </td>",
"       <td>",
"        10 mg/kg/dose orally three to four times daily (maximum 2.4 g/day)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Celecoxib",
"       </td>",
"       <td>",
"        Body weight:",
"        <br/>",
"        10 to 25 kg: 50 mg orally twice daily",
"        <br/>",
"        &gt;25 kg:&nbsp;100 mg orally twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Disease modifying antirheumatic drugs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Methotrexate*",
"       </td>",
"       <td>",
"        10 to 15 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per week or 0.5 to 1 mg/kg/week (maximum 25 mg per week) administered orally* or subcutaneously with folic acid 1 mg daily or leucovorin calcium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sulfasalazine",
"       </td>",
"       <td>",
"        15 to 30 mg/kg/dose orally&nbsp;twice daily, maximum 2 g/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Leflunomide",
"       </td>",
"       <td>",
"        Body weight:",
"        <br/>",
"        &lt;20 kg:&nbsp;10 mg orally every other day",
"        <br/>",
"        20 to 40 kg:&nbsp;10 mg orally&nbsp;daily",
"        <br/>",
"        &gt;40 kg:&nbsp;20 mg orally daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        Third-line agents in uveitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Mycophenolate mofetil",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td class=\"sublist_other\">",
"        12 to 18 mg/kg or 600 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        orally twice per day, maximum 3 g/day",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Cyclosporine (microemulsion)",
"       </td>",
"       <td class=\"sublist_other\">",
"        1.5 to 2.5 mg/kg orally twice per day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Azathioprine",
"       </td>",
"       <td class=\"sublist_other\">",
"        1 to 2 mg/kg orally once per day or in two equal divided doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Tumor necrosis factor (TNF) inhibitors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adalimumab",
"       </td>",
"       <td>",
"        Body weight:",
"        <br/>",
"        15 to 30 kg: 20 mg subcutaneously every 2 weeks",
"        <br/>",
"        &ge;30 kg: 40 mg subcutaneously every 2 weeks (may escalate to weekly for partial response)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Etanercept",
"       </td>",
"       <td>",
"        0.8 mg/kg (maximum 50 mg) subcutaneously weekly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Infliximab",
"       </td>",
"       <td>",
"        5 to 10 mg/kg intravenously every 4-8 weeks (after loading doses)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        Anti-TNF agents without pediatric indication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Certolizumab",
"       </td>",
"       <td class=\"sublist_other\">",
"        200 mg subcutaneously every 2 weeks (after loading doses)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Golimumab",
"       </td>",
"       <td class=\"sublist_other\">",
"        50 mg subcutaneously monthly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        CTLA4-Ig (T cell costimulatory blocker)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Abatacept",
"       </td>",
"       <td>",
"        10 mg/kg (maximum 1000 mg) intravenously every 4 weeks",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <strong>",
"     NOTE: Treatment should be performed by clinicians who are experienced in the use, monitoring, adjustment and drug interactions of these agents because of the potential toxicity of these drugs/combinations and the unpredictability of the disease.",
"    </strong>",
"    <div class=\"footnotes\">",
"     * Subcutaneous administration is suggested for methotrexate doses &gt;10 mg/m",
"     <sup>",
"      2",
"     </sup>",
"     since oral absorption decreases with higher doses.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Mycophenolate mofetil dosing shown is for immediate release oral preparation. Dose adjustment is required for conversion to the delayed-release oral&nbsp;preparation.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
