var title_f22_4_22603="Stages of CKD";
var content_f22_4_22603=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F80596&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F80596&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Stages of chronic kidney disease based on 2002 Kidney Disease Outcomes Quality Initiative",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"       <td class=\"subtitle1\">",
"        GFR (mL/min/1.73 m",
"        <sup>",
"         2",
"        </sup>",
"        )",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        Kidney damage with normal or &uarr; GFR",
"       </td>",
"       <td>",
"        &ge;90",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        Kidney damage with mild &darr; GFR",
"       </td>",
"       <td>",
"        60 to 89",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3",
"       </td>",
"       <td>",
"        Moderage &darr; GFR",
"       </td>",
"       <td>",
"        30 to 59",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4",
"       </td>",
"       <td>",
"        Severe &darr; GFR",
"       </td>",
"       <td>",
"        15 to 29",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5",
"       </td>",
"       <td>",
"        Kidney failure",
"       </td>",
"       <td>",
"        &lt;15 (or dialysis)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Chronic kidney disease is defined as either kidney damage or glomerular filtration rate (GFR) &lt;60 mL/min/1.73 m",
"    <sup>",
"     2",
"    </sup>",
"    for&nbsp;three months. Kidney damage is defined as pathologic abnormalities or markers of damage, including abnormalities in blood or urine tests or imaging studies.",
"    <div class=\"footnotes\">",
"     Adapted from: 2002 National Kidney Foundation. K/DOQI clinical practice guidelines for chronic kidney disease: Evaluation, classification, and stratification. Am J Kidney Dis 2002; 39:S1.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
