var title_f22_40_23179="Causes of unintentional death";
var content_f22_40_23179=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F79294&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F79294&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Leading causes of unintentional-injury deaths, United States, 1998",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Motor Vehicle",
"       </td>",
"       <td>",
"        43,501",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Falls",
"       </td>",
"       <td>",
"        16,274",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Poisoning*",
"       </td>",
"       <td>",
"        10,255",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Drowning",
"       </td>",
"       <td>",
"        4,406",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Choking",
"       </td>",
"       <td>",
"        3,515",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"2\" rowspan=\"1\">",
"        Deaths",
"and injuries in the home",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        29,500",
"fatalities and 7,100,000 disabling injuries in 2000. This represents a",
"5 percent increase over 1999 figures.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        In the home,",
"there is a fatal injury every 18 minutes and a disabling injury every 4",
"seconds.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        The four",
"leading fatal events are solid and liquid poisonings, falls, fires and",
"burns, and suffocation by ingested object.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Includes solid and liquid poisoning only. The leading causes of fatal unintentional injuries in 1998 (latest available figures) are the same top four since 1970. \"Suffocation by ingested object\" has supplanted \"fires and burns\" as the fifth leading cause of death.",
"    </div>",
"    <div class=\"reference\">",
"     National safety Council. Report on Injuries in America, 2001 (file://www.nsc.org/library/rept2000.htm#recs).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
