var title_f22_40_23180="Manifestations of hypothermia";
var content_f22_40_23180=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F55728&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F55728&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical manifestations of accidental hypothermia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mild",
"       </td>",
"       <td class=\"subtitle1\">",
"        Moderate",
"       </td>",
"       <td class=\"subtitle1\">",
"        Severe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Neurologic",
"       </td>",
"       <td>",
"        Confusion, slurred speech, impaired judgment, amnesia",
"       </td>",
"       <td>",
"        Lethargy, hallucinations, loss of pupillary reflexes,",
"EEG abnormalities",
"       </td>",
"       <td>",
"        Loss of cerebrovascular regulation, decline in EEG",
"activity, coma, loss of ocular reflexes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiovascular",
"       </td>",
"       <td>",
"        Tachycardia, increased cardiac output, and systemic",
"vascular resistance",
"       </td>",
"       <td>",
"        Progressive bradycardia (unresponsive to atropine),",
"decreased cardiac output, BP, atrial and ventricularar rhythmias, J",
"(Osborn) wave on EKG",
"       </td>",
"       <td>",
"        Decline in BP and cardiac output, ventricular",
"fibrillation (&lt;28&deg;C; 82.4&deg;F) and asystole",
"(&lt;20&deg;C; 68&deg;F)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Respiratory",
"       </td>",
"       <td>",
"        Tachypnea, bronchorrhea",
"       </td>",
"       <td>",
"        Hypoventilation, decreased oxygen consumption and CO2",
"production, loss of cough reflex",
"       </td>",
"       <td>",
"        Pulmonary edema, apnea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal",
"       </td>",
"       <td>",
"        Bladder atony, cold diuresis",
"       </td>",
"       <td>",
"        Cold diuresis",
"       </td>",
"       <td>",
"        Decreased renal perfusion, oliguria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Musculoskeletal",
"       </td>",
"       <td>",
"        Increased shivering",
"       </td>",
"       <td>",
"        Decreased shivering (&lt;32&deg;C;",
"89.6&deg;F) muscle rigidity",
"       </td>",
"       <td>",
"        Patient may appear dead, \"pseudo-rigor mortis\"",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Metabolic",
"       </td>",
"       <td colspan=\"1\">",
"        Increased metabolic rate,",
"hyperglycemia",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Decreased metabolic rate, hyper- or hypoglycemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hematologic",
"       </td>",
"       <td colspan=\"1\">",
"        Increase in hematocrit, decreased",
"platelet count and white blood cell count, bleeding diathesis, DIC",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gastrointestinal",
"       </td>",
"       <td>",
"        Ileus, pancreatitis, gastric stress ulcers, hepatic",
"dysfunction",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BP: blood pressure; C02: carbon dioxide; DIC: disseminated intravascular coagulation;&nbsp;EEG: electroencephalogram.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from Hanania, NA, Zimmerman, JL. Accidental hypothermia. Critical Care Clinincs 1999; 15:235. Copyright &copy; 1999 WB Saunders Company.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
