var script_f22_41_23195=[""].join("\n");
var outline_f22_41_23195=null;
var title_f22_41_23196="Fast v slow conduction";
var content_f22_41_23196=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F71831&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F71831&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Fast versus slow response cardiac tissues",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Properties",
"       </td>",
"       <td class=\"subtitle1\">",
"        Fast response times",
"       </td>",
"       <td class=\"subtitle1\">",
"        Slow response times",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Location",
"       </td>",
"       <td>",
"        Atria, specialized infranodal conducting system, ventricles, AV bypass tracts",
"       </td>",
"       <td>",
"        Sinoatrial and atrioventricular nodes, depolarized fast response tissues in which phase 0 depends upon calcium current",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Passive cellular properties",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Normal resting potential",
"       </td>",
"       <td>",
"        -80 to -95 mV",
"       </td>",
"       <td>",
"        -40 to -65 mV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Active cellular properties",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Phase 0 current",
"       </td>",
"       <td>",
"        Sodium",
"       </td>",
"       <td>",
"        Primarily calcium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Phase 0 channel kinetics",
"       </td>",
"       <td>",
"        Fast",
"       </td>",
"       <td>",
"        Slow activation; inactivation depends upon voltage and cell calcium concentration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Peak overshoot",
"       </td>",
"       <td>",
"        +20 to +40 mV",
"       </td>",
"       <td>",
"        -5 to + 20 mV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Action potential amplitude",
"       </td>",
"       <td>",
"        90 to 135 mV",
"       </td>",
"       <td>",
"        30 to 70 mV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Properties dependent upon active and passive properties",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Threshold voltage",
"       </td>",
"       <td>",
"        -60 to -75 mV",
"       </td>",
"       <td>",
"        -40 to -60 mV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Conduction velocity",
"       </td>",
"       <td>",
"        0.5 to 5 m/sec",
"       </td>",
"       <td>",
"        0.01 to 0.1 m/sec",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Conducive to reentry",
"       </td>",
"       <td>",
"        Only with inactivation of sodium channels with marked slowing of conduction velocity",
"       </td>",
"       <td>",
"        Present in normal tissue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Automaticity",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Comparison of the major electrophysiologic characteristics of \"fast\" and \"slow\" response tissues.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
