" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 470px\">",
"   <div class=\"ttl\">",
"    Wagner grade 4 ulcer",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 378px; height: 241px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
