" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Techniques for lower extremity amputation",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/43/23225/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?22/43/23225/contributors\">",
"     Venkat Kalapatapu, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/43/23225/contributors\">",
"     Section Editors",
"    </a>",
