" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Coagulopathy associated with trauma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/44/23242/contributors\">",
