var title_f22_44_23244="Clinical features malabsorption";
var content_f22_44_23244=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F76166&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F76166&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Signs and symptoms of malabsorption",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Malabsorption of",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Laboratory findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calories",
"       </td>",
"       <td>",
"        Weight loss with normal appetite",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fat",
"       </td>",
"       <td>",
"        Pale and voluminous stool, diarrhea without flatulence, steatorrhea",
"       </td>",
"       <td>",
"        Stool fat &gt;6 g/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Protein",
"       </td>",
"       <td>",
"        Edema, muscle atrophy, amenorrhea",
"       </td>",
"       <td>",
"        Hypoalbuminemia, hypoproteinemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Carbohydrates",
"       </td>",
"       <td>",
"        Watery diarrhea, flatulence, acidic stool pH, milk intolerance, stool osmotic gap",
"       </td>",
"       <td>",
"        Increased breath hydrogen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin B12",
"       </td>",
"       <td>",
"        Anemia, subacute combined degeneration of the spinal cord (early symptoms are paresthesias and ataxia associated with loss of vibration and position sense)",
"       </td>",
"       <td>",
"        Macrocytic anemia, vitamin B12 decreased, abnormal Schilling test, serum methylmalonic acid and homocysteine increased",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Folic acid",
"       </td>",
"       <td>",
"        Anemia",
"       </td>",
"       <td>",
"        Macrocytic anemia, serum and RBC folate decreased, serum homocysteine increased",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin B, general",
"       </td>",
"       <td>",
"        Cheilosis, painless glossitis, acrodermatitis, angular stomatitis",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Iron",
"       </td>",
"       <td>",
"        Microcytic anemia, glossitis, pagophagia",
"       </td>",
"       <td>",
"        Serum iron and ferritin decreased, total iron binding capacity increased",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calcium and vitamin D",
"       </td>",
"       <td>",
"        Paresthesia, tetany, pathologic fractures due to osteomalacia, positive Chvostek and Trousseau signs",
"       </td>",
"       <td>",
"        Hypocalcemia, serum alkaline phosphatase increased, abnormal bone densitometry",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin A",
"       </td>",
"       <td>",
"        Follicular hyperkeratosis, night blindness",
"       </td>",
"       <td>",
"        Serum retinol decreased",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin K",
"       </td>",
"       <td>",
"        Hematoma, bleeding disorders",
"       </td>",
"       <td>",
"        Prolonged prothrombin time, vitamin K-dependent coagulation factors decreased",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
