var title_f22_45_23261="Biodegradeable soft tissue fillers";
var content_f22_45_23261=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F71066&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F71066&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Examples of biodegradable filling agents*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Filler type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Name",
"       </td>",
"       <td class=\"subtitle1\">",
"        Properties",
"       </td>",
"       <td class=\"subtitle1\">",
"        Uses",
"       </td>",
"       <td class=\"subtitle1\">",
"        Duration",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"6\">",
"        Hyaluronic acid (HA)",
"       </td>",
"       <td>",
"        Restylane",
"        <sup>",
"         &reg;",
"        </sup>",
"        <br/>",
"        Restylane-L",
"        <sup>",
"         &reg;",
"        </sup>",
"        <br/>",
"        Perlane",
"        <sup>",
"         &reg;",
"        </sup>",
"        <br/>",
"        Perlane-L",
"        <sup>",
"         &reg;",
"        </sup>",
"       </td>",
"       <td>",
"        Non-animal HA; cross-linked with BDDE",
"       </td>",
"       <td>",
"        Correction of moderate to severe wrinkles and folds (eg, nasolabial folds); lip enhancement",
"       </td>",
"       <td>",
"        6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Juv&eacute;derm",
"        <sup>",
"         &reg;",
"        </sup>",
"        Ultra",
"        <br/>",
"        Juv&eacute;derm",
"        <sup>",
"         &reg;",
"        </sup>",
"        Ultra XC",
"        <br/>",
"        Juv&eacute;derm",
"        <sup>",
"         &reg;",
"        </sup>",
"        Ultra Plus",
"        <br/>",
"        Juv&eacute;derm",
"        <sup>",
"         &reg;",
"        </sup>",
"        Ultra Plus XC",
"        <br/>",
"        Juv&eacute;derm",
"        <sup>",
"         &reg;",
"        </sup>",
"        Voluma",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        Non-animal, cross-linked HA; XC formulations contain lidocaine",
"       </td>",
"       <td>",
"        Correction of moderate to severe wrinkles and folds (eg, nasolabial folds)",
"       </td>",
"       <td>",
"        6 to 12 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Elevess&trade;",
"       </td>",
"       <td>",
"        Non-animal HA + lidocaine; cross-linked with BCDI, sodium metabisulfite preservative",
"       </td>",
"       <td>",
"        Correction of moderate to severe wrinkles and folds (eg, nasolabial folds)",
"       </td>",
"       <td>",
"        12 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prevelle",
"        <sup>",
"         &reg;",
"        </sup>",
"        Silk",
"       </td>",
"       <td>",
"        Non-animal HA + lidocaine",
"       </td>",
"       <td>",
"        Correction of moderate to severe wrinkles and folds (eg, nasolabial folds)",
"       </td>",
"       <td>",
"        6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Teosyal",
"        <sup>",
"         &reg;",
"        </sup>",
"       </td>",
"       <td>",
"        Non-animal HA, cross-linked with BDDE; reduced protein and bacterial endotoxin level, no lidocaine",
"       </td>",
"       <td>",
"        Correction of fine lines to deeper rhytides and facial sculpting",
"       </td>",
"       <td>",
"        6 to 12 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Revanesse",
"        <sup>",
"         &reg;",
"        </sup>",
"        Ultra",
"       </td>",
"       <td>",
"        Non-animal HA, cross-linked with BDDE",
"       </td>",
"       <td>",
"        Correction of moderate to deep rhytides, as well as lipoatrophy and facial wasting",
"       </td>",
"       <td>",
"        6 to 12 months",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Calcium hydroxylapatite microspheres (CaHA)",
"       </td>",
"       <td>",
"        Radiesse",
"        <sup>",
"         &reg;",
"        </sup>",
"       </td>",
"       <td>",
"        Synthetic CaHA microspheres (25-45 &micro;m) suspended in an aqueous carboxymethylcellulose gel carrier",
"       </td>",
"       <td>",
"        For correction of moderate-to-severe facial wrinkles and folds (eg, nasolabial folds); for the correction of facial lipoatrophy",
"       </td>",
"       <td>",
"        12 months",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Poly-L-lactic acid (PLLA)",
"       </td>",
"       <td>",
"        Sculptra",
"        <sup>",
"         &reg;",
"        </sup>",
"       </td>",
"       <td rowspan=\"2\">",
"        Microparticles of PLLA induce fibroblastic activity; PLLA particles are eventually metabolized",
"       </td>",
"       <td>",
"        For restoration and/or correction of facial lipoatrophy",
"       </td>",
"       <td rowspan=\"2\">",
"        12 to 24 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sculptra",
"        <sup>",
"         &reg;",
"        </sup>",
"        Aesthetic",
"       </td>",
"       <td>",
"        For correction of shallow to deep nasolabial fold contour deficiencies and other facial wrinkles",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Autologous fat",
"       </td>",
"       <td>",
"        n/a",
"       </td>",
"       <td>",
"        Harvesting and reinjection of fat",
"       </td>",
"       <td>",
"        Dermal/lip augmentation",
"       </td>",
"       <td>",
"        Months to years",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Platelet-rich fibrin matrices (PRFM)",
"       </td>",
"       <td>",
"        SELPHYL",
"        <sup>",
"         &reg;",
"        </sup>",
"        System",
"       </td>",
"       <td>",
"        PRFM generated through collection and centrifugation of blood; stimulates cell proliferation and skin rejuvenation over time",
"       </td>",
"       <td>",
"        Glabellar lines, nasolabial folds, acne or post-operative scars, full-face contouring, and other dermal defects",
"       </td>",
"       <td>",
"        3 months to years",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BDDE: butanediol diglycidyl ether; BCDI: biscarbodiimide.",
"     <br/>",
"     * Collagen-based filling agents are additional biodegradeable fillers.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     The duration of filler effect tends to be longer in the upper face and mid face than in the perioral region.",
"     <br/>",
"     &Delta; Pending FDA approval as XC formulation.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
