var title_f22_5_22619="Criteria for extubation or decannulation to continuous NPPV";
var content_f22_5_22619=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F64672&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F64672&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Indications and criteria for extubation or decannulation of patients unable to sustain autonomous breathing",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        The patient is mentally competent, cooperative, and not using heavy sedation or narcotics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Supplemental oxygen therapy is avoided or minimized",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        SaO",
"        <sub>",
"         2",
"        </sub>",
"        is maintained above 95 percent by aggressive airway secretion elimination and assisted ventilation as necessary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bulbar muscle function is sufficient so that aspiration of airway secretions does not cause O",
"        <sub>",
"         2",
"        </sub>",
"        desaturation below 95 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        There is no history of substance abuse or uncontrollable seizures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary compliance is adequate for noninvasive IPPV to provide normal alveolar ventilation at interface pressures under 40 cm H",
"        <sub>",
"         2",
"        </sub>",
"        O",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        There are no conditions which interfere with the use of IPPV interfaces, ie, facial fractures, inadequate bite or neck rotation for mouthpiece entry, presence of a nasogastric tube or a beard that hampers airtight interface seal, etc",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
