var script_f22_50_23329=[" ",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function BODE_fx() {",
"with(document.BODE_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1[0].checked){",
"Score = Score + 0;",
"}",
"if (cc1[1].checked){",
"Score = Score + 1;",
"}",
"if (cc1[2].checked){",
"Score = Score + 2;",
"}",
"if (cc1[3].checked){",
"Score = Score + 3;",
"}",
"if (cc2[0].checked){",
"Score = Score + 0;",
"}",
"if (cc2[1].checked){",
"Score = Score + 1;",
"}",
"if (cc2[2].checked){",
"Score = Score + 2;",
"}",
"if (cc2[3].checked){",
"Score = Score + 3;",
"}",
"if (cc3[0].checked){",
"Score = Score + 0;",
"}",
"if (cc3[1].checked){",
"Score = Score + 0;",
"}",
"if (cc3[2].checked){",
"Score = Score + 1;",
"}",
"if (cc3[3].checked){",
"Score = Score + 2;",
"}",
"if (cc3[4].checked){",
"Score = Score + 3;",
"}",
"if (cc4[0].checked){",
"Score = Score + 0;",
"}",
"if (cc4[1].checked){",
"Score = Score + 1;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"rrclr();",
"if ((Score >= 0) && (Score <= 2)){ document.getElementById('rr1_1').bgColor = '#cccccc';",
"}",
"if ((Score >= 3) && (Score <= 4)){ document.getElementById('rr1_2').bgColor = '#cccccc';",
"}",
"if ((Score >= 5) && (Score <= 6)){ document.getElementById('rr1_3').bgColor = '#cccccc';",
"}",
"if ((Score >= 7) && (Score <= 10)){ document.getElementById('rr1_4').bgColor = '#cccccc';",
"}",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"document.getElementById('rr1_4').bgColor = \"\";",
"}",
"",
"",
""].join("\n");
