" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Problems with solute clearance and ultrafiltration in continuous peritoneal dialysis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/50/23337/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?22/50/23337/contributors\">",
"     John M Burkart, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?22/50/23337/contributors\">",
"     William L Henrich, MD, MACP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/50/23337/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?22/50/23337/contributors\">",
"     Thomas A Golper, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/50/23337/contributors\">",
"     Deputy Editor",
"    </a>",
