var title_f22_51_23355="Lumbosacral radiculopathies";
var content_f22_51_23355=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F75974&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F75974&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Symptoms and signs of lumbosacral radiculopathies",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Nerve",
"       </td>",
"       <td class=\"subtitle1\">",
"        Symptoms",
"       </td>",
"       <td class=\"subtitle1\">",
"        Signs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        L5",
"       </td>",
"       <td>",
"        Back pain radiating down the lateral leg to foot",
"       </td>",
"       <td>",
"        Decreased foot dorsiflexion, toe extension, foot inversion and eversion; mild weakness of leg abduction in severe cases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        S1",
"       </td>",
"       <td>",
"        Pain radiating down the posterior leg to foot; leg pain greater than back pain",
"       </td>",
"       <td>",
"        Decreased leg extension, foot inversion, plantar flexion, and toe flexion; decreased sensation in the posterior leg and lateral foot; loss of ankle jerk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        L2-4",
"       </td>",
"       <td>",
"        Acute back pain radiating around the anterior leg into the knee and possibly foot",
"       </td>",
"       <td>",
"        Decreased hip flexion, knee extension, leg abduction; decreased sensation in the anterior thigh down the medial aspect of the shin; diminished knee jerk in severe cases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        S2-4",
"       </td>",
"       <td>",
"        Sacral or buttock pain radiating down the posterior leg or into the perineum",
"       </td>",
"       <td>",
"        Minimal weakness; bowel and bladder dysfunction",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
