"if(Length >= 99.5 && Length < 100){L = -0.3521; M = 15.0275; S = 0.08177;};",
"if(Length >= 100.0 && Length < 100.5){L = -0.3521; M = 15.1637; S = 0.08198;};",
"if(Length >= 100.5 && Length < 101){L = -0.3521; M = 15.3018; S = 0.08220;};",
"if(Length >= 101.0 && Length < 101.5){L = -0.3521; M = 15.4419; S = 0.08243;};",
