var title_f22_55_23421="Immunosuppressive agents used in heart transplantation";
var content_f22_55_23421=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F50962&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F50962&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Immunosuppressive agents used in heart transplantation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dosing",
"       </td>",
"       <td class=\"subtitle1\">",
"        Target levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        Major toxicities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Calcineurin inhibitors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cyclosporine",
"       </td>",
"       <td>",
"        4-8 mg/kg/day in 2 divided doses, titrated to keep target 12-hr trough levels",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          0-6 months:",
"         </strong>",
"        </p>",
"        <p>",
"         250-350 ng/mL",
"        </p>",
"        <p>",
"         <strong>",
"          6-12 months:",
"         </strong>",
"        </p>",
"        <p>",
"         200-250 ng/mL",
"        </p>",
"        <p>",
"         <strong>",
"          &gt;12 months:",
"         </strong>",
"        </p>",
"        <p>",
"         100-200 ng/mL",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Renal insufficiency",
"        </p>",
"        <p>",
"         Hypertension",
"        </p>",
"        <p>",
"         Dyslipidemia",
"        </p>",
"        <p>",
"         Hypokalemia and hypomagnesemia",
"        </p>",
"        <p>",
"         Hyperurecemia",
"        </p>",
"        <p>",
"         Neurotoxicity (encephalopathy, seizures, tremors, neuropathy)",
"        </p>",
"        <p>",
"         Gingival hyperplasia",
"        </p>",
"        <p>",
"         Hirsutism",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tacrolimus",
"       </td>",
"       <td>",
"        0.05-0.1 mg/kg/day in 2 divided doses, titrated to keep target 12-hr trough levels",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          0-6 months:",
"         </strong>",
"        </p>",
"        <p>",
"         10-15 ng/mL",
"        </p>",
"        <p>",
"         <strong>",
"          6-12 months:",
"         </strong>",
"        </p>",
"        <p>",
"         5-10 ng/mL",
"        </p>",
"        <p>",
"         <strong>",
"          &gt;12 months:",
"         </strong>",
"        </p>",
"        <p>",
"         5-10 ng/mL",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Renal dysfunction",
"        </p>",
"        <p>",
"         Hypertension",
"        </p>",
"        <p>",
"         Hyperglycemia and diabetes mellitus",
"        </p>",
"        <p>",
"         Dyslipidemia",
"        </p>",
"        <p>",
"         Hyperkalemia",
"        </p>",
"        <p>",
"         Hypomagnesemia",
"        </p>",
"        <p>",
"         Neurotoxicity (tremors, headaches)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Cell cycle agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Azathioprine",
"       </td>",
"       <td>",
"        1.5-3.0 mg/kg/day, titrated to keep WBC &sim;3K",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        <p>",
"         Bone marrow suppression",
"        </p>",
"        <p>",
"         Hepatitis (rare)",
"        </p>",
"        <p>",
"         Pancreatitis",
"        </p>",
"        <p>",
"         Malignancy",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mycophenolate mofetil",
"       </td>",
"       <td>",
"        1000-3000 mg/day in 2 divided doses",
"       </td>",
"       <td>",
"        Mycophenolic acid (MPA): 2-5 mcg/ml",
"       </td>",
"       <td>",
"        <p>",
"         Gastrointestinal disturbances (nausea, gastritis, and diarrhea)",
"        </p>",
"        <p>",
"         Leukopenia",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Proliferation signal inhibitors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sirolimus",
"       </td>",
"       <td>",
"        1-3 mg/day, titrated to keep therapeutic 24-hr trough levels",
"       </td>",
"       <td>",
"        5-10 ng/mL",
"       </td>",
"       <td>",
"        <p>",
"         Oral ulcerations",
"        </p>",
"        <p>",
"         Hypercholesterolemia and hypertriglyceridemia",
"        </p>",
"        <p>",
"         Poor wound healing",
"        </p>",
"        <p>",
"         Lower extremity edema",
"        </p>",
"        <p>",
"         Pulmonary toxicities (pneumonitis, alveolar hemorrhage)",
"        </p>",
"        <p>",
"         Leukopenia, anemia, and thrombocytopenia",
"        </p>",
"        <p>",
"         Potentiation of CNI nephrotoxicity",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Corticosteroids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prednisone",
"       </td>",
"       <td>",
"        1 mg/kg/day in 2 divided doses, tapered to 0.05 mg/kg/day by 6-12 mo",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        <p>",
"         Weight gain",
"        </p>",
"        <p>",
"         Hypertension",
"        </p>",
"        <p>",
"         Hyperlipidemia",
"        </p>",
"        <p>",
"         Osteopenia",
"        </p>",
"        <p>",
"         Hyperglycemia",
"        </p>",
"        <p>",
"         Poor wound healing",
"        </p>",
"        <p>",
"         Salt and water retention",
"        </p>",
"        <p>",
"         Proximal myopathy",
"        </p>",
"        <p>",
"         Cataracts",
"        </p>",
"        <p>",
"         Peptic ulcer disease",
"        </p>",
"        <p>",
"         Growth retardation",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Pham MX, Chen JM, Berry GJ, Rose EA, Schroeder JS. Surgical Treatment of Heart Failure, Cardiac Transplantation, and Mechanical Ventricular Support. In: Hurst's The Heart, 12th ed, V, Fuster R, O'Rourke R, Walsh P, Poole-Wilson (eds). McGraw-Hill, New York 2007. Copyright &copy; 2007 McGraw-Hill Companies Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
