var script_f22_56_23425=[" ",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function MDACC_fx() {",
"with(document.MDACC_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1[0].checked){",
"Score = Score + 0;",
"}",
"if (cc1[1].checked){",
"Score = Score + 1;",
"}",
"if (cc1[2].checked){",
"Score = Score + 2;",
"}",
"if (cc2.checked){",
"Score = Score + 2;",
"}",
"if (cc3.checked){",
"Score = Score + 1;",
"}",
"if (cc4[0].checked){",
"Score = Score + 0;",
"}",
"if (cc4[1].checked){",
"Score = Score + 1;",
"}",
"if (cc4[2].checked){",
"Score = Score + 2;",
"}",
"if (cc4[3].checked){",
"Score = Score + 3;",
"}",
"if (cc5.checked){",
"Score = Score + 2;",
"}",
"if (cc6.checked){",
"Score = Score + 2;",
"}",
"if (cc7[0].checked){",
"Score = Score + 0;",
"}",
"if (cc7[1].checked){",
"Score = Score + 1;",
"}",
"if (cc7[2].checked){",
"Score = Score + 2;",
"}",
"if (cc8.checked){",
"Score = Score + 3;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"rrclr();",
"if ((Score >= 0) && (Score <= 4)){ document.getElementById('rr1_1').bgColor = '#cccccc';",
"}",
"if ((Score >= 5) && (Score <= 6)){ document.getElementById('rr1_2').bgColor = '#cccccc';",
"}",
"if ((Score >= 7) && (Score <= 8)){ document.getElementById('rr1_3').bgColor = '#cccccc';",
"}",
"if ((Score >= 9) && (Score <= 17)){ document.getElementById('rr1_4').bgColor = '#cccccc';",
"}",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"document.getElementById('rr1_4').bgColor = \"\";",
"}",
"",
"",
""].join("\n");
