var title_f22_58_23467="Clin manif RT fibrosis";
var content_f22_58_23467=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F78310&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F78310&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Common clinical manifestations of radiation-induced fibrosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Tissues",
"       </td>",
"       <td class=\"subtitle1\">",
"        Manifestations and symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Skin, subcutaneous tissue",
"       </td>",
"       <td>",
"        Induration, thickening, impaired cosmesis, loss of elasticity, shrinkage, limited joint mobility, lymphedema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lung",
"       </td>",
"       <td>",
"        Restrictive pulmonary symptoms, dyspnea, impaired lung function, oxygen dependency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Head and neck",
"       </td>",
"       <td>",
"        Trismus, mucosal fibrosis, ulcerations, fistula",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gastrointestinal tract",
"       </td>",
"       <td>",
"        Obstruction, dysphagia, ulceration, fistula, diarrhea, pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urinary tract",
"       </td>",
"       <td>",
"        Ureteral and urethral stenosis, bladder shrinkage, urgency, increased voiding frequency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Female genital tract",
"       </td>",
"       <td>",
"        Loss of reproductive function, pain, rigidness, shortness and narrowness of vagina, dyspareunia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Muscles",
"       </td>",
"       <td>",
"        Muscle atrophy, muscle shortening, reduced mobility, decreased strength",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
