var title_f22_60_23499="Common Terminology Criteria for Adverse Events diarrhea";
var content_f22_60_23499=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F65649&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F65649&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    National Cancer Institute common terminology criteria for diarrhea",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1_single\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1",
"       </td>",
"       <td>",
"        Increase of less than four stools per day over baseline; mild increase in ostomy output compared to baseline",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2",
"       </td>",
"       <td>",
"        Increase of four to six stools per day over baseline; moderate increase in ostomy output compared to baseline",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3",
"       </td>",
"       <td>",
"        Increase of seven or more stools per day over baseline; incontinence; hospitalization indicated; severe increase in ostomy output compared to baseline; limiting self care activities of daily living",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4",
"       </td>",
"       <td>",
"        Life-threatening consequences; urgent intervention indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        5",
"       </td>",
"       <td>",
"        Death",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     With permission from: Common Terminology Criteria for Adverse Events (CTCAE), version 4.03, June 2010, National Institutes of Health, National Cancer Institute, file://evs.nci.nih.gov/ftp1/CTCAE/CTCAE_4.03_2010-06-14_QuickReference_5x7.pdf.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
