var title_f22_62_23532="Monitoring immunosuppressives";
var content_f22_62_23532=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F58710&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F58710&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suggested monitoring for immunosuppressive drugs",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug or class",
"       </td>",
"       <td class=\"subtitle1\">",
"        Toxicities",
"       </td>",
"       <td class=\"subtitle1\">",
"        Monitoring",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Glucocorticoids",
"       </td>",
"       <td>",
"        Hypertension, hyperglycemia, hyperlipidemia, cataracts, and osteoporosis",
"       </td>",
"       <td>",
"        Periodic blood pressure, serum glucose, lipid profile, eye exam, and bone densitometry",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Methotrexate",
"       </td>",
"       <td>",
"        Myelosuppression and hepatotoxicity, including cirrhosis",
"       </td>",
"       <td>",
"        CBC, liver enzymes, albumin, creatinine every 1 to 2 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hydroxychloroquine",
"       </td>",
"       <td>",
"        Macular damage",
"       </td>",
"       <td>",
"        Fundoscopic and visual field exams every 6 to 12 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sulfasalazine",
"       </td>",
"       <td>",
"        Myelosuppression",
"       </td>",
"       <td>",
"        CBC monthly for 3 months, then every 3 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Azathioprine",
"       </td>",
"       <td>",
"        Myelosuppression, hepatotoxicity, and lymphoproliferative disorders",
"       </td>",
"       <td>",
"        CBC, liver enzymes every 1 to 2 months (CBC every 1 to 2 weeks with change in dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cyclophosphamide",
"       </td>",
"       <td>",
"        Myelosuppression, myeloproliferative disorders, hemorrhagic cystitis, bladder cancer, and other neoplasms",
"       </td>",
"       <td>",
"        CBC, urinalysis every 2 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chlorambucil",
"       </td>",
"       <td>",
"        Myelosuppression, myeloproliferative disorders, and other neoplasms",
"       </td>",
"       <td>",
"        CBC every 2 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cyclosporine and FK 506",
"       </td>",
"       <td>",
"        Renal insufficiency, anemia, and hypertension",
"       </td>",
"       <td>",
"        Blood pressure and creatinine every month (every 2 weeks with change in dose), periodic CBC, potassium, and liver enzymes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Leflunomide",
"       </td>",
"       <td>",
"        Diarrhea, alopecia, and asymptomatic liver transaminase elevations",
"       </td>",
"       <td>",
"        CBC, liver enzymes, and creatinine every 1 to 2 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Etanercept",
"       </td>",
"       <td>",
"        Infection, injection site reactions",
"       </td>",
"       <td>",
"        PPD prior to therapy; periodic CBC",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infliximab",
"       </td>",
"       <td>",
"        Infection",
"       </td>",
"       <td>",
"        PPD prior to therapy; periodic CBC",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
