var title_f22_63_23547="Hypoxia altitude test";
var content_f22_63_23547=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F54344&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F54344&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Interpreting results of hypoxia altitude simulation testing*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        PaO",
"        <sub>",
"         2",
"        </sub>",
"        (mmHg)",
"       </td>",
"       <td class=\"subtitle1\">",
"        PaO",
"        <sub>",
"         2",
"        </sub>",
"        (kPa)",
"       </td>",
"       <td class=\"subtitle1\">",
"        SpO",
"        <sub>",
"         2",
"        </sub>",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommendation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &gt;55 mmHg",
"       </td>",
"       <td>",
"        &gt;7.4 kPa",
"       </td>",
"       <td>",
"        &gt;88 percent",
"       </td>",
"       <td>",
"        In-flight oxygen not advised",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        50-55 mmHg",
"       </td>",
"       <td>",
"        6.6-7.4 kPa",
"       </td>",
"       <td>",
"        84-88 percent",
"       </td>",
"       <td>",
"        Borderline, a walk test may be helpful",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &lt;50 mmHg",
"       </td>",
"       <td>",
"        &lt;6.6 kPa",
"       </td>",
"       <td>",
"        &lt;84 percent",
"       </td>",
"       <td>",
"        In-flight oxygen advised",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PaO",
"     <sub>",
"      2",
"     </sub>",
"     : arterial partial pressure of oxygen; SpO",
"     <sub>",
"      2",
"     </sub>",
"     : oxygen saturation by pulse oximetry.",
"     <br>",
"      * Hypoxia altitude simulation testing involves breathing a gas mixture of nitrogen with oxygen at 15.1 percent for 20 minutes, as tolerated.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
