var title_f22_63_23548="Treatment of complicated vaginal candidiasis";
var content_f22_63_23548=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F50932&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F50932&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of complicated vaginal candidiasis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Severe vaginitis symptoms&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oral fluconazole 150 mg every 72 hours for 2 or 3 doses (depending on severity)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         &nbsp;&nbsp;&nbsp;&nbsp;OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Topical antifungal therapy daily for 7 days. A&nbsp;",
"        <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"         low potency topical corticosteroid can be applied to the vulva for 48 hours to relieve symptoms&nbsp;until the antifungal drug&nbsp;exerts its&nbsp;effect.",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Recurrent vulvovaginal candidiasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Induction (3 doses of fluconazole 150 mg every 72 hours until asymptomatic) followed by maintenance fluconazole 150 mg once per week for 6 months. If fluconazole is not feasible, options include 10 to 14 days of a topical or oral azole followed by maintenance therapy for 6 months (eg, clotrimazole 200 mg vaginal cream twice weekly or 500 mg vaginal suppository once weekly)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Non-albicans Candida vaginitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Therapy depends upon species identified:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C. glabrata: intravaginal boric acid* 600 mg daily for 14 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;&nbsp;&nbsp;&nbsp;If failure occurs: 17 percent flucytosine 5 grams nightly for 14 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C. krusei: intravaginal clotrimazole, miconazole, or terconazole for 7 to 14 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        All other species: Conventional dose fluconazole",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Compromised&nbsp;host (eg,",
"        <span style=\"font-family: verdana; font-size: 13px;\">",
"         poorly controlled diabetes, immunosuppression, debilitation) and",
"        </span>",
"        Candida isolate susceptible to azoles&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oral or topical therapy for 7 to 14 days",
"        <span class=\"subtitle1_single\">",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Pregnancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"         Intravaginal&nbsp;clotrimazole or miconazole for&nbsp;7 days",
"        </span>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <p>",
"     Boric acid capsules and flucytosine cream are not commercially available, but can be made by a compounding pharmacy.",
"    </p>",
"    <div class=\"footnotes\">",
"     <p>",
"      &nbsp;* Boric acid capsules can be fatal if swallowed",
"     </p>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
