" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Tourette syndrome",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?22/8/22664/contributors\">",
