var title_f23_1_23580="Pretest probability of DVT";
var content_f23_1_23580=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F75871&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F75871&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pretest probability of deep vein thrombosis (Wells score)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical feature",
"       </td>",
"       <td class=\"subtitle1\">",
"        Score",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Active cancer (treatment ongoing or within the previous 6 months or palliative)",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Paralysis, paresis, or recent plaster immobilization of the lower extremities",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recently bedridden for more than 3 days or major surgery, within 4 weeks",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Localized tenderness along the distribution of the deep venous system",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Entire leg swollen",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calf swelling by more than 3 cm when compared to the asymptomatic leg (measured below tibial tuberosity)",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pitting edema (greater in the symptomatic leg)",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Collateral superficial veins (nonvaricose)",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Alternative diagnosis as likely or more likely than that of deep venous thrombosis",
"       </td>",
"       <td>",
"        -2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\" rowspan=\"1\">",
"        Score",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High probability",
"       </td>",
"       <td>",
"        &nbsp;3 or greater",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Moderate probability",
"       </td>",
"       <td>",
"        1 or 2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low probability",
"       </td>",
"       <td>",
"        0 or less",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Modification:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        This",
"clinical model has been modified to take one other clinical feature",
"into account: a previously documented deep vein thrombosis (DVT) is",
"given the score of 1. Using this modified scoring system, DVT is either",
"likely or unlikely, as follows:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        DVT likely",
"       </td>",
"       <td>",
"        2 or greater&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        DVT unlikely",
"       </td>",
"       <td>",
"        1 or less",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Wells, PS, Anderson, DR, Bormanis, J, et al, Lancet 1997; 350:1795 and Wells, PS, Anderson, DR, Rodger, M, et al. N Engl J Med 2003; 349:1227.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
