" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Anaplastic thyroid cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/10/23721/contributors\">",
