var title_f23_12_23755="Antimicrobial therapy impetigo";
var content_f23_12_23755=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F78882&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F78882&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Empiric antimicrobial therapy for impetigo (excluding MRSA)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       &nbsp;",
"      </td>",
"      <td class=\"subtitle1\">",
"       Adults",
"      </td>",
"      <td class=\"subtitle1\">",
"       Children*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Oral therapy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Dicloxacillin",
"      </td>",
"      <td>",
"       500 mg orally every six hours",
"      </td>",
"      <td>",
"       &nbsp;25 mg/kg/day in 4 divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Cephalexin",
"      </td>",
"      <td>",
"       500 mg orally every six hours",
"      </td>",
"      <td>",
"       &nbsp;25 mg/kg/day in 4 divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Clindamycin&bull;",
"      </td>",
"      <td>",
"       300 mg orally every six hours",
"      </td>",
"      <td>",
"       &nbsp;15-25 mg/kg/day in 3 divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Erythromycin&Delta;",
"      </td>",
"      <td>",
"       250 mg orally every six hours",
"      </td>",
"      <td>",
"       &nbsp;40 mg/kg/day in 4 divided doses",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Maximum single dose should not exceed dose for adults.",
"     <br>",
"      &bull; Note local prevalence of inducible clindamycin resistance; see text.",
"      <br>",
"       &Delta; Macrolides may not be adequate therapy given increasing resistance among S. pyogenes and S. aureus; note local resistance patterns.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
