var title_f23_16_23819="Differential diagnosis of tall (peaked) T waves";
var content_f23_16_23819=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F73040&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F73040&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential diagnosis of tall (peaked) T waves",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Ischemic causes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperacute phase of myocardial infarction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute transient transmural ischemia (Prinzmetal's angina)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic (evolving) phase of myocardial infarction (tall positive T waves reciprocal to primary deep T wave inversions)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Nonischemic causes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal variants (\"early repolarization\" patterns)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperkalemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute hemopericardium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cerebrovascular hemorrhage (more commonly T wave inversions)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Left ventricular hypertrophy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Right precordial leads, usually in conjunction with left precordial ST depressions and T wave inversions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Left precordial leads, particularly in association with \"diastolic\" volume overload conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Left bundle branch block (right precordial leads)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute pericarditis (occasionally)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
