var content_f23_18_23852=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F56405&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F56405&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical neurophysiology classification of myoclonus",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Cortical",
"       </td>",
"       <td class=\"subtitle1\">",
"        Cortical-subcortical",
"       </td>",
"       <td class=\"subtitle1\">",
"        Subcortical-nonsegmental",
"       </td>",
"       <td class=\"subtitle1\">",
"        Segmental",
"       </td>",
"       <td class=\"subtitle1\">",
"        Peripheral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         EEG",
"        </strong>",
"       </td>",
"       <td>",
"        Variable; some with grossly visible epileptiform discharges and slow waves",
"       </td>",
"       <td>",
"        Generalized spike and wave",
"       </td>",
"       <td>",
"        No consistent findings",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         EMG",
"        </strong>",
"       </td>",
"       <td>",
"        Bursts typically &lt;75 ms",
"       </td>",
"       <td>",
"        Bursts &lt;100 ms",
"       </td>",
"       <td>",
"        Variable burst duration",
"       </td>",
"       <td>",
"        Bursts typically &gt;100 ms",
"       </td>",
"       <td>",
"        Variable burst duration; irregular periodicity of discharges is typical",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Back-averaged EEG time-locked to EMG",
"        </strong>",
"       </td>",
"       <td>",
"        Time-locked correlation almost always present; focal sharp wave 10 to 40 ms before myoclonic jerk is common",
"       </td>",
"       <td>",
"        Time-locked correlation is typical",
"       </td>",
"       <td>",
"        No association",
"       </td>",
"       <td>",
"        No association",
"       </td>",
"       <td>",
"        No association",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         SEPs",
"        </strong>",
"       </td>",
"       <td>",
"        Enlarged cortical component in many cases",
"       </td>",
"       <td>",
"        Enlarged cortical component possible",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Long latency EMG reflex response",
"        </strong>",
"       </td>",
"       <td>",
"        Variable; enhanced long latency reflex (C reflex) typical with cortical reflex myoclonus",
"       </td>",
"       <td>",
"        Some cases have C reflex at rest",
"       </td>",
"       <td>",
"        Some cases have reflex response to sound",
"       </td>",
"       <td>",
"        Variable; some are very short latency and incompatible with supraspinal origin",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     EEG: electroencephalography; EMG: surface electromyography; SEPs: somatosensory evoked potentials.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Caviness, JN, Brown, P. Myoclonus: current concepts and recent advances. Lancet Neurol 2004; 3:598.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
