var title_f23_19_23867="Diagnostic criteria juvenile myelomonocytic leukemia";
var content_f23_19_23867=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F85788&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F85788&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for juvenile myelomonocytic leukemia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Peripheral blood monocytosis &gt;1x10",
"        <sup>",
"         9",
"        </sup>",
"        /L",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blasts (including promonocytes)* are &gt;20 percent of the leukocytes in the blood and of the nucleated bone marrow cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No Ph chromosome or",
"        <em>",
"         BCR-ABL1",
"        </em>",
"        fusion gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plus two or more of the following:",
"        <br/>",
"        <ul>",
"         <li>",
"          Hemoglobin F increased for age",
"         </li>",
"         <li>",
"          Immature granulocytes in the peripheral blood",
"         </li>",
"         <li>",
"          WBC count &gt;10x10",
"          <sup>",
"           9",
"          </sup>",
"          /L",
"         </li>",
"         <li>",
"          Clonal chromosomal abnormality (may be monosomy 7)",
"         </li>",
"         <li>",
"          GM-CSF hypersensitivity of myeloid progenitors",
"          <em>",
"           in vitro",
"          </em>",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * In this classification, promonocytes are equivalent to blasts.",
"    </div>",
"    <div class=\"reference\">",
"     From Swerdlow SH, Campo E, Harris NL, Jaffe ES, Pileri SA, Stein H, Thiele J, Vardiman JW. World Health Organization Classification of Tumours of Haematopoietic and Lymphoid Tissues. IARC, Lyon, 2008. Reproduced with permission. Copyright &copy; 2008 IARC.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
