var title_f23_19_23868="Phases of management for enterocutaneous fistulae";
var content_f23_19_23868=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=SURG%2F57027&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=SURG%2F57027&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Phases of management for enterocutaneous fistulae",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Phase",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"       <td class=\"subtitle1\">",
"        Timing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stabilization",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Rehydration",
"         </li>",
"         <li>",
"          Correction of anemia",
"         </li>",
"         <li>",
"          Electrolyte repletion",
"         </li>",
"         <li>",
"          Drainage of sepsis",
"         </li>",
"         <li>",
"          Control of fistula drainage",
"         </li>",
"         <li>",
"          Local skin care measures",
"         </li>",
"         <li>",
"          Commencement of nutritional support",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        24-48 h",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Investigation",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Fistulogram to define anatomy",
"         </li>",
"         <li>",
"          CT/US/MRI to localize collections and guide drainage",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        7-10 d",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decision",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Assess likelihood of nonoperative closure",
"         </li>",
"         <li>",
"          Plan therapeutic course",
"         </li>",
"         <li>",
"          Decide optimal surgical timing",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        7-10 d to 4-6 wk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Definitive therapy",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Plan operative approach",
"         </li>",
"         <li>",
"          Bowel resection with end-to-end anastomosis",
"         </li>",
"         <li>",
"          Secure abdominal closure (+/- flap)",
"         </li>",
"         <li>",
"          Gastrostomy",
"         </li>",
"         <li>",
"          Jejunostomy",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        4-6 wk or if spontaneous closure unlikely",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Healing",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Continue nutrition support",
"         </li>",
"         <li>",
"          Transition to total oral/enteral feedings",
"         </li>",
"         <li>",
"          Physical and emotional rehabilitation",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        5-10 d after closure",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CT: computed tomography; US: ultrasound; MRI: magnetic resonance imaging.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Fischer JE, Evenson AR. Gastrointestinal-cutaneous fistulae. In: Mastery of Surgery, Fischer JE (Ed), Lippincott Williams &amp; Wilkins 2007. Copyright &copy; 2007 Lippincott Williams &amp; Wilkins.",
"    </div>",
"    <div class=\"contractual\">",
"     <br/>",
"     <a href=\"file://www.lww.com\">",
"      file://www.lww.com",
"     </a>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
