" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical manifestations and diagnosis of acute rheumatic fever",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/20/23879/contributors\">",
