var title_f23_22_23917="Imaging guidelines from COG for Ewing family of tumors";
var content_f23_22_23917=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F68393&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F68393&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for imaging for Ewing sarcoma family of tumors from the Children's Oncology Group (all imaging studies are required unless otherwise noted)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        At presentation and prior to local control",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Site",
"       </td>",
"       <td class=\"subtitle2_single\">",
"        Testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Primary and bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Primary tumor",
"       </td>",
"       <td class=\"indent1\">",
"        MRI with gadolinium (presurgery exam should be performed within four weeks of local control procedure)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        MRI with gadolinium or CT scan with contrast (recommended for possible bone metastases if diagnosis uncertain)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Chest",
"       </td>",
"       <td class=\"indent1\">",
"        CT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs (recommended at presentation only)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Whole body",
"       </td>",
"       <td class=\"indent1\">",
"        Technetium bone scintigraphy and/or FDG-PET (recommended at presentation, particularly if primary bone tumor negative on bone scintigraphy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Baseline after local treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Site",
"       </td>",
"       <td class=\"subtitle2_single\">",
"        Testing and timing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Primary and bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs within two weeks of surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Primary tumor",
"       </td>",
"       <td class=\"indent1\">",
"        MRI with gadolinium or CT scan with IV contrast (in patients without significant metallic artifact at primary tumor site) three to four months after local control",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        MRI with gadolinium or CT scan with contrast (recommended for possible bone metastases if diagnosis uncertain)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Surveillance on chemotherapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Site",
"       </td>",
"       <td class=\"subtitle2_single\">",
"        Testing and timing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Primary and bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs after 10 cycles (approximately halfway through postoperative chemotherapy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        MRI with gadolinium or CT scan with IV contrast recommended if symptoms or abnormal imaging (and surgical intervention or radiation therapy contemplated)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chest",
"       </td>",
"       <td class=\"indent1\">",
"        CT after 10 cycles (approximately halfway through postoperative chemotherapy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Whole body",
"       </td>",
"       <td class=\"indent1\">",
"        Technetium bone scintigraphy (if disease positive on prior bone scintigraphy) or FDG-PET (if disease positive on prior FDG-PET and bone scan-negative) recommended",
"        <br/>",
"        <ul>",
"         <li>",
"          At end of cytotoxic chemotherapy",
"         </li>",
"         <li>",
"          Sooner if symptoms or abnormal imaging (and surgical or other intervention contemplated)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Surveillance postchemotherapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Site",
"       </td>",
"       <td class=\"subtitle2_single\">",
"        Testing and timing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Primary and bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs every three months x 8, then every six months x 6, then every 12 months x 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        MRI with gadolinium or CT scan with IV contrast recommended if symptoms or abnormal imaging (and surgical or other intervention contemplated)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Chest",
"       </td>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs every three months x 8, then every six months x 6, then every 12 months x 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CT recommended if abnormal radiographs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Whole body",
"       </td>",
"       <td class=\"indent1\">",
"        Technetium bone scintigraphy (if positive on prior scans) or FDG-PET (if positive on prior scans and bone scan-negative) if symptoms or abnormal imaging AND surgical or other intervention contemplated",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AP: anterior-posterior; CT: computed tomography scan; FDG-PET: fluorodeoxyglucose positron emission tomography; MRI: magnetic resonance imaging.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Meter JS, Nadel HR, Marina N, et al. Pediatr Blood Cancer 2008; 51:63.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
