" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Injectable soft tissue fillers: Semipermanent agents",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/23/23930/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/23/23930/contributors\">",
"     Alastair Carruthers, FRCPC",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/23/23930/contributors\">",
"     Jean Carruthers, MD, FRCSC",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/23/23930/contributors\">",
"     Shannon Humphrey, MD, FRCPC, FAAD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/23/23930/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/23/23930/contributors\">",
"     Jeffrey S Dover, MD, FRCPC",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/23/23930/contributors\">",
"     Charles E Butler, MD, FACS",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/23/23930/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/23/23930/contributors\">",
"     Abena O Ofori, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"UTD.htm?23/23/23930/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
"  <div id=\"reviewProcess\">",
"   <span>",
"    All topics are updated as new evidence becomes available and our",
"   </span>",
